function mapInitialize() {
    var e = [{
            featureType: "landscape",
            elementType: "labels",
            stylers: [{
                visibility: "off"
            }]
        }, {
            featureType: "transit",
            elementType: "labels",
            stylers: [{
                visibility: "off"
            }]
        }, {
            featureType: "poi",
            elementType: "labels",
            stylers: [{
                visibility: "off"
            }]
        }, {
            featureType: "water",
            elementType: "labels",
            stylers: [{
                visibility: "off"
            }]
        }, {
            featureType: "road",
            elementType: "labels.icon",
            stylers: [{
                visibility: "off"
            }]
        }, {
            stylers: [{
                hue: "#00aaff"
            }, {
                saturation: -200
            }, {
                gamma: .5
            }, {
                lightness: 12
            }]
        }, {
            featureType: "road",
            elementType: "labels.text.fill",
            stylers: [{
                visibility: "on"
            }, {
                lightness: 24
            }]
        }, {
            featureType: "road",
            elementType: "geometry",
            stylers: [{
                lightness: 57
            }]
        }],
        t = {
            zoom: 12,
            center: new google.maps.LatLng(52.00298, 4.269249),
            mapTypeId: google.maps.MapTypeId.ROADMAP,
            mapTypeControl: !1,
            panControl: !1,
            zoomControl: !1,
            scaleControl: !1,
            streetViewControl: !1,
            styles: e
        },
        l = new google.maps.Map(document.getElementById("google-map"), t),
        a = new google.maps.LatLng(52.00248, 4.369249);
    new google.maps.Marker({
        position: a,
        map: l,
        icon: "images/my-location.png"
    });
    $(document).ready(function() {
        google.maps.event.addDomListener(window, "resize", mapInitialize)
    })
}